import{_ as e,c as o,o as a,ag as r}from"./chunks/framework.BfE-mKMG.js";const p=JSON.parse('{"title":"Configuration","description":"Jira connection and credential settings","frontmatter":{"title":"Configuration","description":"Jira connection and credential settings"},"headers":[],"relativePath":"guide/configuration.md","filePath":"guide/configuration.md","lastUpdated":null}'),n={name:"guide/configuration.md"};function i(s,t,d,l,c,h){return a(),o("div",null,[...t[0]||(t[0]=[r('<h1 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-label="Permalink to &quot;Configuration&quot;">​</a></h1><p>This guide covers how to configure Canopy to connect to your Jira instance.</p><h2 id="jira-cloud" tabindex="-1">Jira Cloud <a class="header-anchor" href="#jira-cloud" aria-label="Permalink to &quot;Jira Cloud&quot;">​</a></h2><h3 id="creating-an-api-token" tabindex="-1">Creating an API Token <a class="header-anchor" href="#creating-an-api-token" aria-label="Permalink to &quot;Creating an API Token&quot;">​</a></h3><ol><li>Go to <a href="https://id.atlassian.com/manage-profile/security/api-tokens" target="_blank" rel="noreferrer">Atlassian Account Settings</a></li><li>Click <strong>Create API token</strong></li><li>Enter a label (e.g., &quot;Canopy&quot;)</li><li>Copy the generated token</li></ol><div class="warning custom-block"><p class="custom-block-title">Keep Your Token Safe</p><p>The token is shown only once. Store it securely.</p></div><h3 id="connection-settings" tabindex="-1">Connection Settings <a class="header-anchor" href="#connection-settings" aria-label="Permalink to &quot;Connection Settings&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Setting</th><th>Value</th></tr></thead><tbody><tr><td><strong>Instance Type</strong></td><td>Jira Cloud</td></tr><tr><td><strong>Jira URL</strong></td><td><code>https://your-domain.atlassian.net</code></td></tr><tr><td><strong>Email</strong></td><td>Your Atlassian account email</td></tr><tr><td><strong>API Token</strong></td><td>The token you created above</td></tr></tbody></table><h2 id="jira-server-data-center" tabindex="-1">Jira Server / Data Center <a class="header-anchor" href="#jira-server-data-center" aria-label="Permalink to &quot;Jira Server / Data Center&quot;">​</a></h2><h3 id="creating-a-personal-access-token-pat" tabindex="-1">Creating a Personal Access Token (PAT) <a class="header-anchor" href="#creating-a-personal-access-token-pat" aria-label="Permalink to &quot;Creating a Personal Access Token (PAT)&quot;">​</a></h3><ol><li>Go to your Jira profile settings</li><li>Navigate to <strong>Personal Access Tokens</strong></li><li>Click <strong>Create token</strong></li><li>Set a name and expiration date</li><li>Copy the generated token</li></ol><h3 id="connection-settings-1" tabindex="-1">Connection Settings <a class="header-anchor" href="#connection-settings-1" aria-label="Permalink to &quot;Connection Settings&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Setting</th><th>Value</th></tr></thead><tbody><tr><td><strong>Instance Type</strong></td><td>Jira Server</td></tr><tr><td><strong>Jira URL</strong></td><td><code>https://jira.your-company.com</code></td></tr><tr><td><strong>Username</strong></td><td>Your Jira username</td></tr><tr><td><strong>Token/Password</strong></td><td>PAT or your password</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Using a Personal Access Token is recommended over username/password for better security.</p></div><h2 id="cors-proxy-configuration" tabindex="-1">CORS Proxy Configuration <a class="header-anchor" href="#cors-proxy-configuration" aria-label="Permalink to &quot;CORS Proxy Configuration&quot;">​</a></h2><p>If using the included proxy server (web app only):</p><table tabindex="0"><thead><tr><th>Setting</th><th>Value</th></tr></thead><tbody><tr><td><strong>Proxy URL</strong></td><td><code>http://localhost:3001/jira</code></td></tr></tbody></table><p>See <a href="/jira-hierarchy-viewer/getting-started/installation#cors-proxy">Installation Guide</a> for proxy setup instructions.</p><div class="info custom-block"><p class="custom-block-title">Desktop App</p><p>The desktop app connects directly to Jira without requiring a CORS proxy.</p></div><h2 id="data-storage" tabindex="-1">Data Storage <a class="header-anchor" href="#data-storage" aria-label="Permalink to &quot;Data Storage&quot;">​</a></h2><p>All configuration is stored in your browser&#39;s localStorage:</p><table tabindex="0"><thead><tr><th>Data</th><th>Storage Key</th><th>Description</th></tr></thead><tbody><tr><td>Connection</td><td><code>canopy_connection</code></td><td>Jira URL, credentials, instance type</td></tr><tr><td>Queries</td><td><code>canopy_jql_queries</code></td><td>Saved JQL queries with titles and colors</td></tr><tr><td>Filters</td><td><code>canopy_filters</code></td><td>Active filter states per query</td></tr><tr><td>Theme</td><td><code>canopy_theme</code></td><td>Light/dark/system preference</td></tr><tr><td>Color Theme</td><td><code>canopy_color_theme</code></td><td>Accent color preference</td></tr><tr><td>Display Density</td><td><code>canopy_display_density</code></td><td>Comfortable/compact view</td></tr><tr><td>Field Config</td><td><code>canopy_field_config</code></td><td>Visible fields on issue cards</td></tr><tr><td>Sort Config</td><td><code>canopy_sort_config</code></td><td>Sort field and direction</td></tr></tbody></table><h2 id="import-export" tabindex="-1">Import/Export <a class="header-anchor" href="#import-export" aria-label="Permalink to &quot;Import/Export&quot;">​</a></h2><p>Backup and transfer your configuration between devices.</p><h3 id="export" tabindex="-1">Export <a class="header-anchor" href="#export" aria-label="Permalink to &quot;Export&quot;">​</a></h3><ol><li>Click the <strong>Settings</strong> icon in the header</li><li>Select <strong>Export Configuration</strong></li><li>Save the JSON file</li></ol><h3 id="import" tabindex="-1">Import <a class="header-anchor" href="#import" aria-label="Permalink to &quot;Import&quot;">​</a></h3><ol><li>Click the <strong>Settings</strong> icon in the header</li><li>Select <strong>Import Configuration</strong></li><li>Choose your JSON backup file</li></ol><p><strong>Included in export:</strong></p><ul><li>Saved JQL queries</li><li>Filter states</li><li>Display preferences</li><li>Theme settings</li></ul><p><strong>Not included (for security):</strong></p><ul><li>Connection credentials</li><li>API tokens</li></ul><h2 id="clearing-data" tabindex="-1">Clearing Data <a class="header-anchor" href="#clearing-data" aria-label="Permalink to &quot;Clearing Data&quot;">​</a></h2><p>To reset all settings:</p><ol><li>Click the <strong>Settings</strong> icon</li><li>Select <strong>Clear All Data</strong></li><li>Confirm the action</li></ol><p>This removes all localStorage data and returns Canopy to its initial state.</p><h2 id="connection-troubleshooting" tabindex="-1">Connection Troubleshooting <a class="header-anchor" href="#connection-troubleshooting" aria-label="Permalink to &quot;Connection Troubleshooting&quot;">​</a></h2><p>See <a href="/jira-hierarchy-viewer/reference/troubleshooting">Troubleshooting</a> for common connection issues and solutions.</p>',38)])])}const u=e(n,[["render",i]]);export{p as __pageData,u as default};
