import{_ as e,c as a,o,ag as r}from"./chunks/framework.BfE-mKMG.js";const u=JSON.parse('{"title":"Data Storage","description":"How Canopy stores your data","frontmatter":{"title":"Data Storage","description":"How Canopy stores your data"},"headers":[],"relativePath":"reference/storage.md","filePath":"reference/storage.md","lastUpdated":null}'),i={name:"reference/storage.md"};function s(n,t,l,d,c,p){return o(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="data-storage" tabindex="-1">Data Storage <a class="header-anchor" href="#data-storage" aria-label="Permalink to &quot;Data Storage&quot;">​</a></h1><p>Canopy stores all configuration locally in your browser or on your filesystem (desktop app).</p><h2 id="web-version-localstorage" tabindex="-1">Web Version (localStorage) <a class="header-anchor" href="#web-version-localstorage" aria-label="Permalink to &quot;Web Version (localStorage)&quot;">​</a></h2><p>All data is stored in browser localStorage under the following keys:</p><table tabindex="0"><thead><tr><th>Key</th><th>Description</th></tr></thead><tbody><tr><td><code>canopy_connection</code></td><td>Jira URL, credentials, instance type</td></tr><tr><td><code>canopy_jql_queries</code></td><td>Saved JQL queries with titles and colors</td></tr><tr><td><code>canopy_filters</code></td><td>Active filter states per query</td></tr><tr><td><code>canopy_theme</code></td><td>Light/dark/system preference</td></tr><tr><td><code>canopy_color_theme</code></td><td>Accent color preference</td></tr><tr><td><code>canopy_display_density</code></td><td>Comfortable/compact view</td></tr><tr><td><code>canopy_field_config</code></td><td>Visible fields on issue cards</td></tr><tr><td><code>canopy_sort_config</code></td><td>Sort field and direction</td></tr><tr><td><code>canopy_sidebar_width</code></td><td>Sidebar width in pixels</td></tr><tr><td><code>canopy_change_checkpoints</code></td><td>Change tracking checkpoints</td></tr><tr><td><code>canopy_auto_refresh</code></td><td>Auto-refresh interval setting</td></tr></tbody></table><h3 id="storage-limits" tabindex="-1">Storage Limits <a class="header-anchor" href="#storage-limits" aria-label="Permalink to &quot;Storage Limits&quot;">​</a></h3><ul><li>localStorage limit: ~5-10 MB (browser dependent)</li><li>Canopy typically uses &lt; 100 KB</li><li>Issue data is not cached (fetched fresh from Jira)</li></ul><h3 id="clearing-data" tabindex="-1">Clearing Data <a class="header-anchor" href="#clearing-data" aria-label="Permalink to &quot;Clearing Data&quot;">​</a></h3><p><strong>Clear all Canopy data:</strong></p><ol><li>Settings &gt; Clear All Data</li></ol><p><strong>Clear via browser:</strong></p><ol><li>Open Developer Tools (F12)</li><li>Application &gt; Storage &gt; Local Storage</li><li>Delete keys starting with <code>canopy_</code></li></ol><h2 id="desktop-version-file-storage" tabindex="-1">Desktop Version (File Storage) <a class="header-anchor" href="#desktop-version-file-storage" aria-label="Permalink to &quot;Desktop Version (File Storage)&quot;">​</a></h2><p>The desktop app stores data in a JSON file:</p><table tabindex="0"><thead><tr><th>Platform</th><th>Path</th></tr></thead><tbody><tr><td>macOS</td><td><code>~/Library/Application Support/com.canopy.jira-viewer/settings.json</code></td></tr><tr><td>Windows</td><td><code>%APPDATA%\\com.canopy.jira-viewer\\settings.json</code></td></tr><tr><td>Linux</td><td><code>~/.config/com.canopy.jira-viewer/settings.json</code></td></tr></tbody></table><h3 id="backup" tabindex="-1">Backup <a class="header-anchor" href="#backup" aria-label="Permalink to &quot;Backup&quot;">​</a></h3><p>Copy the <code>settings.json</code> file to back up your configuration.</p><h3 id="reset" tabindex="-1">Reset <a class="header-anchor" href="#reset" aria-label="Permalink to &quot;Reset&quot;">​</a></h3><p>Delete the <code>settings.json</code> file to reset to defaults.</p><h2 id="security-considerations" tabindex="-1">Security Considerations <a class="header-anchor" href="#security-considerations" aria-label="Permalink to &quot;Security Considerations&quot;">​</a></h2><h3 id="credentials" tabindex="-1">Credentials <a class="header-anchor" href="#credentials" aria-label="Permalink to &quot;Credentials&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Credentials are stored in plain text in localStorage/settings.json. This is standard for browser-based applications but consider the security implications.</p></div><p><strong>Mitigations:</strong></p><ul><li>Use API tokens instead of passwords</li><li>Set token expiration dates</li><li>Revoke tokens if device is compromised</li></ul><h3 id="export-security" tabindex="-1">Export Security <a class="header-anchor" href="#export-security" aria-label="Permalink to &quot;Export Security&quot;">​</a></h3><p>When exporting configuration:</p><ul><li><strong>Included:</strong> Queries, filters, preferences</li><li><strong>Not included:</strong> Credentials, API tokens</li></ul><p>This allows safe sharing of query configurations with team members.</p><h2 id="data-portability" tabindex="-1">Data Portability <a class="header-anchor" href="#data-portability" aria-label="Permalink to &quot;Data Portability&quot;">​</a></h2><h3 id="export-import" tabindex="-1">Export/Import <a class="header-anchor" href="#export-import" aria-label="Permalink to &quot;Export/Import&quot;">​</a></h3><ol><li>Settings &gt; Export Configuration</li><li>Save JSON file</li><li>On new device: Settings &gt; Import Configuration</li></ol><h3 id="manual-migration" tabindex="-1">Manual Migration <a class="header-anchor" href="#manual-migration" aria-label="Permalink to &quot;Manual Migration&quot;">​</a></h3><p>Copy localStorage keys or settings.json between devices.</p><h2 id="data-isolation" tabindex="-1">Data Isolation <a class="header-anchor" href="#data-isolation" aria-label="Permalink to &quot;Data Isolation&quot;">​</a></h2><ul><li>Each browser profile has separate localStorage</li><li>Each desktop installation has its own settings file</li><li>Multiple Jira instances can be configured by switching connections</li></ul>',35)])])}const g=e(i,[["render",s]]);export{u as __pageData,g as default};
